import axios from 'axios';
import {useEffect, useState } from "react";
//import image from "./portal_image.jpeg";

function Student() {
    const [studentid, setId] = useState('');
    const [studentname, setName] = useState("");
    const [studentaddress, setAddress] = useState("");
    const [mobile, setMobile] = useState("");
    const [students, setUsers] = useState([]);

    useEffect(() => {
        (async () => await Load())();
        }, []);

    async function  Load()
    {   
       const result = await axios.get(
           "http://localhost:8081/api/v1/student/getall");
           setUsers(result.data);
           console.log(result.data);
    }



    async function save(event)
    {
        event.preventDefault();
        try{
            await axios.post("http://localhost:8081/api/v1/student/save",
            {
            studentname: studentname,
            studentaddress: studentaddress,
            mobile: mobile
            });
            alert("Student Registation Successfully");
            setId("");
            setName("");
            setAddress("");
            setMobile("");
            Load();
        }
        catch(err){
            alert("User Registation Failed");
        }
    }


    async function editStudent(students)
    {
        setName(students.studentname);
        setAddress(students.studentaddress);
        setMobile(students.mobile); 
        setId(students._id);
    }

    async function DeleteStudent(studentid)
    {
        await axios.delete("http://localhost:8081/api/v1/student/delete/" + studentid); 
        alert("Student deleted Successfully");
        Load();
    }

    async function update(event)
    {
        event.preventDefault();
        try{
            await axios.put("http://localhost:8081/api/v1/student/edit/" + studentid ,
            {

            studentname: studentname,
            studentaddress: studentaddress,
            mobile: mobile

            });
            alert("Registation Updateddddd");
            setId("");
            setName("");
            setAddress("");
            setMobile("");
            Load();
        }
        catch(err){
        alert("Student Updateddd Failed");
        }
    }

    return (
        <div style={{
            backgroundImage: `url("https://via.placeholder.com/")`,
            width: "100%",
            height: "100vh"
          }}>
            <h1 align="center" >Student Details</h1>
            <div class="container mt-4" >
                <form>
                    <div class="form-group">
                        <label>Student Name</label>
                        <input  type="text" class="form-control" id="studentname"
                        value={studentname}
                        onChange={(event) =>
                            {
                            setName(event.target.value);      
                            }}
                        />
                    </div>
                    <div class="form-group">
                        <label>Student Address</label>
                        <input  type="text" class="form-control" id="studentaddress" 
                            value={studentaddress}
                            onChange={(event) =>
                                {
                                    setAddress(event.target.value);      
                                }}
                        />
                    </div>
                    <div class="form-group">
                        <label>Mobile</label>
                        <input type="text" class="form-control" id="mobile" 
                            value={mobile}
                        onChange={(event) =>
                            {
                            setMobile(event.target.value);      
                            }}
                        />
                    </div>
                    <div>
                        <button   class="btn btn-info btn-color btn-bg-color btn-sm col-xs-2 "  onClick={save}>Register</button>
                        <button   class="btn btn-success btn-color btn-bg-color btn-sm col-xs-2 margin-left"  onClick={update}>Update</button>
                    </div>   
                </form>
            </div>
            <br/>
            <table class="table table-dark" align="center">
                <thead>
                    <tr>
                        <th scope="col">Student Name</th>
                        <th scope="col">Student Address</th>
                        <th scope="col">Student Mobile</th>
                        <th scope="col">Option</th>
                    </tr>
                </thead>
                {students.map(function fn(student)
                {
                    return(
                        <tbody>
                            <tr>
                                <td>{student.studentname}</td>
                                <td>{student.studentaddress}</td>
                                <td>{student.mobile}</td>        
                                <td>
                                    <button type="button" class="btn btn-warning"  onClick={() => editStudent(student)} >Edit</button>  
                                    <button type="button" class="btn btn-danger" onClick={() => DeleteStudent(student._id)}>Delete</button>
                                </td>
                            </tr>
                        </tbody>
                    );
                })}
            </table>
        </div>
    );
  }

export default Student;
